import styled from "styled-components";

export const Button = styled.button`
    background-color: ${(props) =>
        props.outlined ? "transparent" : "var(--white)"};
    border-radius: ${(props) =>
        props.rounded ? "50rem" : "5px"
    };
    border: 1px solid ${(props) =>
        props.outlined ? "var(--outline)" : "var(--white)"
    };
    color: ${(props) =>
        props.outlined ? "var(--outline)" : "var(--primary)"
    };

    cursor: pointer;
    padding: 5px 26px;

    font-size: 15px;
    font-weight: bold;

    &:hover{
        background-color: ${(props) =>
            props.outlined ? "var(--dark-hover)" : "var(--light-hover)"
        };
    }
`;
