import styled from "styled-components";

export const Container = styled.div`
    margin: 0 auto;
    max-width: 1100px;
    display: flex;

    justify-content: center;
    flex-wrap: wrap;
    padding: 0 3%;
`;
export const Card = styled.div`
    padding: 7px 5px;
    display: flex;
    flex-direction: column;

    align-items: center;
    text-align: center;

    margin: 0 10px;
    margin-top: 20px;
    width: 300px;
    border-radius: 15px;

    box-shadow: 0 1px 2px gray;

    &::nth-last-child()
`;
export const Image = styled.div`
    border-radius: 12px;

    width: 100%;
    height: 320px;
    background-color: #222632;
`;
