import styled from "styled-components";

export const Container = styled.div`
    margin: 0 auto;
    margin-bottom: 110px;
    max-width: 1100px;
    display: flex;

    justify-content: center;
    flex-wrap: wrap;
    padding: 0 3%;
`;
export const Card = styled.div`
    padding: 7px 5px;
    display: flex;
    flex-direction: column;

    align-items: center;
    text-align: center;

    margin: 0 10px;
    margin-top: 20px;
    width: 300px;
    border-radius: 15px;

    box-shadow: 0 1px 2px gray;
`;
export const Image = styled.div`
    border-radius: 12px;

    width: 100%;
    height: 320px;
    background-color: #222632;
`;
export const CardPrice = styled.div`
    margin: 10px 0;
    padding: 5px 19px;

    border-radius: 12px;
    background-color: #ff4500;

    color: white;
    font-weight: bold;
`;
export const CardName = styled.div`
    font-size: 16px;
    font-weight: 500;

    margin: 10px 0 ;
`;
export const Front = styled.div`
    position: relative;
    z-index: 1;
    
    transform: translateY(50px);
    width: 100%;
`
export const Back = styled.div`
   transform: translateY(-50px);
    width: 100%;
`
