import styled, { css } from "styled-components";
import { UserSecret, PersonCircle } from "../../styles/Icons";

export const Container = styled.div`
    position: relative;
    z-index: 2;

    padding: 0 min(60px, 7vw);
    background-color: var(--secondary);

`;

const cssIcon = css`
    color: var(--white);
    fill: var(--white);

`;
export const LogoIcon = styled(UserSecret)`
    width: 45px;
    height: 45px;
    ${cssIcon}
`;
export const LogoBread = styled.div`
    user-select: none;

    > span {
        margin-left: 18px;

        font-weight: bold;
        font-size: 26px;
        line-height: 0;
        letter-spacing: 1px;
        color: var(--white);
    }
`;
export const LoginSide = styled.div`
    border:1px solid var(--white);

    display: flex;
    align-items: center;

    height: 40px;
     * {
        height: 100%;
    }

    > button {
        margin-right: 26px;
    }
`;

export const Input = styled.input`
    

    width: 200px;
    border: 0;
`;

export const PersonIcon = styled(PersonCircle)`
    ${cssIcon}
    margin-left: 12px;

    width: 36px;
    height: 36px;
`;

export const ToHead = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;

    padding: 10px 0 20px;
    border-bottom: 1px solid var(--outline);
`;
export const BoHead = styled.div``;
