import styled, { css } from "styled-components";
import { UserSecret, PersonCircle } from "../../styles/Icons";

export const Container = styled.div`
    position: relative;
    z-index: 2;

    padding: 0 min(60px, 7vw);
    background-color: var(--secondary);

`;

const cssIcon = css`
    fill: var(--white);

`;
export const LogoIcon = styled(UserSecret)`
    stroke-width: 13px;
    stroke: var(--green);

    width: 45px;
    height: 45px;

    color: transparent;
`;
export const LogoBread = styled.div`
    user-select: none;

    > span {
        margin-left: 18px;

        font-weight: bold;
        font-size: 26px;
        line-height: 0;
        letter-spacing: 1px;
        color: var(--white);
    }
`;
export const LoginSide = styled.div`

    display: flex;
    align-items: center;

    height: 40px;
     * {
        height: 100%;
    }

    > button {
        margin-right: 10px;
    }
`;

export const Input = styled.input`
    padding: 0 30px;

    width: 200px;
    border-radius: 5px;
    border: 0;
`;

export const PersonIcon = styled(PersonCircle)`
    ${cssIcon}
    margin-left: 12px;

    width: 36px;
    height: 36px;

`;

export const WrapperInput = styled.div`
    border: 1px solid white;
    position: relative;
    > label{
        top: 50%;
        left: 20%;
        transform: translateY(-50%);
        color: var(--gray);
        position: absolute;
    }
`;
export const ToHead = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;

    padding: 10px 0 20px;
    border-bottom: 1px solid var(--outline);
`;
export const BoHead = styled.div``;
