import React, { useEffect, useState } from "react";
import ProdutosList from "./ProdutosList";
import Loading from "./Loading";

import { Container, Centralize } from "./styles/ProdutosApiStyle";

export default function ProdutosApi() {
    const [produto, setProduto] = useState([]);

    useEffect(() => {
        async function fetchProdutos(url) {
            const response = await fetch(url);
            const data = await response.json();

            setProduto(data);
        }
        fetchProdutos(
            "http://makeup-api.herokuapp.com/api/v1/products.json?brand=covergirl&product"
        );
    }, []);

    return (
        <Container>
            <Centralize>
                <h2>Produtos</h2>
            </Centralize>
            <socialNetwork>
                <Social>
                    <FacebookIcon />
                    <a href="https://www.facebook.com/marcos.proenca.186/"></a>
                </Social>
                <Social>
                    <LinkedinIcon />
                    a
                </Social>
                <GitHubIcon />

            </socialNetwork>
            {produto.length ? <ProdutosList produtos={produto} /> : <Loading />}
        </Container>
    );
}
